<?phpclass Model_Post extends Model{		function post_data($data)	{			$db = new PDO('mysql:host=localhost;dbname=test', 'root');		$num = $db->query("SELECT SUM(posts_num) FROM threads");		$num = $num->fetch();		$num = $num[0]+1;//повторяющийся код		$db->exec("INSERT INTO posts VALUES($num, $data->issue_time, '$data->post', $data->thread)");		$db->exec("UPDATE threads SET posts_num = posts_num + 1 WHERE op_post=$data->thread");		$db->exec("UPDATE threads SET speed = posts_num / ((".time()." - issue_time)/3600)");		//$db->exec("INSERT INTO posts VALUES($num, ".time().", '$data[1]', $num)");		foreach($db->errorInfo() as $ko)			echo $ko;	}}